tinymce.PluginManager.add('shortmet', function (editor, url) {

  // 注册一个工具栏按钮名称
  editor.ui.registry.addButton('shortmet', {
    text: '短期',
    onAction: function () {
      var csrf = $('input[name="csrfmiddlewaretoken"]').val();
      $.ajax({
        url: "tinymce/shortmet",
        type: "post",
        timeout: 2500,
        data: {
          'csrfmiddlewaretoken':csrf
        },
        dataType: "json",
        success: function (response) {
          editor.insertContent(response.data);       
        }
      });
      
      //openDialog();
    }
  });
});


