tinymce.PluginManager.add('openfile', function (editor, url) {
  var openDialog = function (datalist) {
    return editor.windowManager.open({
      title: '打开文件',
      body: {
        type: 'panel',
        items: [
          {
            type: 'grid', // component type
            columns: 4, // number of columns
            items: [
              {
                type: 'selectbox', // component type
                name: 'type', // identifier
                label: '材料类型',
                disabled: true, // disabled state
                size: 1, // number of visible values (optional)
                items: datalist
              },
              {
                type: 'selectbox', // component type
                name: 'year', // identifier
                label: '年份',
                disabled: true, // disabled state
                size: 1, // number of visible values (optional)
                items: datalist
              },
              {
                type: 'selectbox', // component type
                name: 'unity', // identifier
                label: '部门',
                disabled: true, // disabled state
                size: 1, // number of visible values (optional)
                items: datalist
              },
              {
                type: 'selectbox', // component type
                name: 'button', // identifier
                label: '按钮',
                disabled: true, // disabled state
                size: 1, // number of visible values (optional)
                items: datalist
              }
            ] // array of panel components
          },
          {
            type: 'listbox', // component type
            name: 'restult', // identifier
            label: '查询结果',
            size: 1, // number of visible values (optional)
            items: datalist
          }
        ]
      },
      buttons: [
        {
          type: 'cancel',
          text: '关闭'
        },
        {
          type: 'submit',
          text: '插入',
          primary: true
        }
      ],
      onSubmit: function (api) {
        var data = api.getData();
        // console.log(data)
        // 将输入框内容插入到内容区光标位置

        // $('#test_plot').html("666")
        // editor.insertContent("666");
        // api.close();


      }
    });
  };

  // 注册一个工具栏按钮名称
  editor.ui.registry.addButton('openfile', {
    text: '打开',
    onAction: function () {
      $('#open_item_list').html('')
      var csrf = $('input[name="csrfmiddlewaretoken"]').val();
      $.ajax({
        url: "open_old_doc",  // 请求的地址
        type: "post",  // 请求方式
        timeout: 250, //设置延迟上限
        data: {
          'csrfmiddlewaretoken': csrf
        },
        dataType: "json",
        success: function (data) {
          // console.log(data.type,data.year,data.unity)
          $('#open_select_type').html("")
          for (i = 0; i < data.type.length; i++) {
            var option_type = document.createElement('option');
            option_type.value = data.type[i]
            option_type.innerText = data.type[i]
            $('#open_select_type').append(option_type)
          }
          $('#open_select_year').html("")
          for (i = 0; i < data.year.length; i++) {
            var option_year = document.createElement('option');
            option_year.value = data.year[i]
            option_year.innerText = data.year[i]
            $('#open_select_year').append(option_year)
          }
          $('#open_select_unity').html("")
          for (i = 0; i < data.unity.length; i++) {
            var option_unity = document.createElement('option');
            option_unity.value = data.unity[i]
            option_unity.innerText = data.unity[i]
            $('#open_select_unity').append(option_unity)
          }

        }
      })
      $('#modal-000002').click()




    }
  });



});




